var servo = require('servo-control');

var TIME_TO_MOVE = 2000;

module.exports = {
  snapImage: function(callback)
  {
    // return both motors to the home position
    servo.returnHome(MOTOR.VER, function() {

    });
    servo.returnHome(MOTOR.HOR, function() {

    });

    // assume both motors are in the home position
    var posHor = MOTOR.HOR.HOME;
    var posVer = MOTOR.VER.HOME;
    // schedule all of the movements
    for (i = 0; i < servo.TOTAL_DEGREES / MOTOR.DEG_PER_IMG; ++i) {
      setTimeout(function() {
        servo.moveMotor(MOTOR.HOR, posHor += MOTOR.DEG_PER_IMG * servo.ONE_DEGREE,
           function() {

        });
      }, i * TIME_TO_MOVE);
    }
  }
}
